
public class Character {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char[] a= {'a','e','i','o','u'};
		for(char b:a){
			System.out.println(b);
		}
		System.out.println("----------------------------------------");
		char[] b= {'A','E','I','O','U'};
		for(char c:b) {
			System.out.println(c);
		}

	}

}
